function Exam2()  # Output formatting  format('short')  # Set up function matrix and Jacobian matrix  f = @(x) [x(1)+x(2)+x(3)-3; x(1).^2+x(2).^2+x(3).^2-5; e.^x(1)+(x(1)*x(2))-(x(1)*x(3))-1]  df = @(x) [1 1 1; 2*x(1) 2*x(2) 2*x(3); e.^x(1)+x(2)-x(3) x(1) -x(1)]  # Intial values  x = [0.1 1.2 2.5]  for i=1:20    # The result of GaussPivotExam is the correction matrix H defined in the text    x = x + GaussPivotExam(df(x), -f(x));    f(x);    endfor;  # Output the results  printf("Zeros using x = [0.1 1.2 2.5]:\n")  disp(x)  # Change inital values  x = [1 0 1];  for i=1:20    # The result of GaussPivotExam is the correction matrix H defined in the text    x = x + GaussPivotExam(df(x), -f(x));    f(x);    endfor;  # Output the results  printf("Zeros using x = [1 0 1]:\n")  disp(x)endfunction