function Gauss(A, B)  a_original = A  b_original = B  i = 0  j = 0  k = 0  xmult = 0.0  sum = 0.0  x = 0  # Forward Elimination  for k=1:length(A)-1    for i=(k+1):length(A)      xmult = A(i, k)/A(k, k)      A(i, :) = A(i, :) - xmult*(A(k, :))      B(i, :) = B(i, :) - xmult*(B(k, :))      endfor    endfor  # Back Substitution  x(1, length(A)) = B(k, :)/A(k, k)  for i=length(A):-1:1    sum = B(i, :)    for j=i+1:length(A)      sum = sum - A(i, j)*x(1, j)      endfor    x(1, i) = sum/A(i,i)    endfor  printf("Solution to the set of linear equations:\n")  disp(x)  printf("Residual vector:\n")  r = a_original*(x.') - b_original;  disp(r)  endfunction