function NewtonPoly(n, t)  # Determine coefficients of the polynomial  i = 0;  j = 0;  x = [2 3 5 6];  y = [1.5713 1.5719 1.5738 1.5731];  a = [0 0 0 0];  for i = 1:n    a(i) = y(i);    endfor  for j = 1:n    for i = n:-1:j+1      a(i) = (a(i) - a(i-1))/(x(i)-x(i-j));      endfor    endfor      # Evaluate the polynomial at point t  temp = a(n);  for i = (n-1):-1:1    temp = (temp)*(t - x(i)) + a(i);    endfor  printf("f(%f) = %f\n", t, temp)endfunction      