function NewtonPoly_Midterm()  # Determine coefficients of the polynomial    # Evaluate the polynomial at 41 different points  f = @(x) (x.^2 + 1).^-1;  x = -5:10/40:5;  y = f(x);    # Set up variables  i = 0;  j = 0;  n = 41;  a = zeros(1, 41);    # Copy y values into a  for i = 1:n    a(i) = y(i);    endfor    # Calculate the interpolation polynomial  for j = 1:n    for i = n:-1:j+1      a(i) = (a(i) - a(i-1))/(x(i)-x(i-j));      endfor    endfor      # Evaluate the interpolation polynomial at 101 points on the interval [0, 5]  avg = 0.0;  for j = 0:5/100:5    temp = a(n);    for i = (n-1):-1:1      temp = (temp)*(j - x(i)) + a(i);      endfor    disp(abs(temp - f(j)))    avg = avg + abs(temp - f(j));    #printf("temp = %f, f(%f) = %f\n", temp, j, f(j))  endfor  avg = avg/101;  printf("Average value of |P(x) - f(x)|: %f\n", avg)endfunction      