function Spline3_Coef()  # Interval of 41 equally spaced knots on [-5, 5]  interval = -5:10/40:5;  # Function  f = @(x) (x.^2 + 1).^-1;  # Create of values from function evaluations  y_val = f(interval);    # Calculate values for h_i and b_i  h = zeros(1, 40);  b = zeros(1, 40);  for i = 1:40    h(i) = interval(i+1) + interval(i);    b(i) = (y_val(i+1) - y_val(i))/h(i);    endfor  #disp(h_i)  #disp(b_i)    # Calvulate u_i and v_i  u = zeros(1, 39);  v = zeros(1, 39);    u(1) = 2*(h(1) + h(2));  v(1) = 6*(b(2) - b(1));    # CHECK THIS LATER  for i = 3:40    u(i-1) = 2*(h(i) + h(i-1)) - (h(i-1).^2 / u(i-2));    v(i-1) = 6*(b(i) - b(i-1)) - ((h(i-1)*v(i-1))/u(i-2));    endfor;  #disp(u_i)  #disp(v_i)    # Calculate z_n  z = zeros(1, 41);  # z_n-1 = v_n-1 / u_n-1  z(40) = v(40)/u(40)  for i = 39:-1:1    z(i) = (v(i) - h(i)*z(i+1))/u(i);    endfor;  z(0) = 0;  disp(z)  endfunction