function z = Spline3_Coef()  # Interval of 41 equally spaced points  interval = -5:10/40:5;  # Function  f = @(x) (x.^2 + 1).^-1;  # Create table of values from function evaluations  y_val = f(interval);    # Calculate values for h_i and b_i  h = zeros(1, 40);  b = zeros(1, 40);  for i = 1:40    h(i) = interval(i+1) + interval(i);    b(i) = (y_val(i+1) - y_val(i))/h(i);    endfor  #disp(h)  #disp(b)    # Calvulate u_i and v_i  u = zeros(1, 40);  v = zeros(1, 40);    u(2) = 2*(h(1) + h(2));  v(2) = 6*(b(2) - b(1));    # CHECK THIS LATER  for i = 3:40    u(i) = 2*(h(i) + h(i-1)) - (h(i-1).^2 / u(i-1));    v(i) = 6*(b(i) - b(i-1)) - ((h(i-1)*v(i-1))/u(i-1));    endfor;  #disp(u)  #disp(v)    # Calculate z_n  z = zeros(1, 42);  # z_n-1 = v_n-1 / u_n-1  z(41) = v(40)/u(40);  for i = 40:-1:2    z(i) = (v(i) - h(i)*z(i+1))/u(i);    endfor;  z(1) = 0;  #disp(z)  endfunction