function Spline3_Eval(interval, y_val, z, x)  i = 0;  h = 0.0;  tmp = 0.0;  interval = -5:10/40:5;  # Function  f = @(x) (x.^2 + 1).^-1;  # Create table of values from function evaluations  y_val = f(interval);    for i = 40:-1:1    if((x - interval(i)) >= 0)      break      endif    endfor    h = interval(i+1) - interval(i);  tmp = (z(i)/2) + ((x - interval(i))*(z(i+1) - z(i)) / (6*h));  tmp = (-1*h/6)*(z(i+1) + 2*z(i)) + (y_val(i+1) - y_val(i))/h + (x - interval(i))*(tmp);  EVAL = y_val(i) + (x - interval(i))*tmp;  disp(EVAL)  endfunction