function SplineEvalTest()  z = Spline3_Coef();   # Interval of 101 equally spaced points on [0, 5]  interval = 0:5/100:5;  # Function  f = @(x) (x.^2 + 1).^-1;  # Create table of values from function evaluations  y_val = f(interval);  average = 0.0;  for i = 0:5/100:5    value = Spline3_Eval(z, i) - f(i);    disp(abs(value))    average = abs(average + value);    endfor  average = average / 101;  printf("Average value of |S(x) - f(x)|: %f\n", average)  endfunction