function Trapezoid()  format('long');  i = 0;  x = 0.0;  n = 102;  a = 0.0;  b = 6.0;  h = (b - a)/n;  f = @(x) RSin(x.^2, 10^-10);  #f = @(x) sin(x.^2);  sum = 0.5 * (f(a) + f(b));  for i=1:(n-1)    x = a + i*h;    sum = sum + f(x);    endfor  sum = sum * h;  printf("Integral of sin(x^2) from 0 to 6 = %f\n", sum)    trapX = 0:6/101:6;  trapY = sin(trapX.^2);  trapsum = trapz(trapX, trapY);  printf("The trapz function calculates the integral of sin(x^2) from 0 to 6 as %f\n", trapsum)endfunction