function TripleIntegral(n)  # Approximating sum should be 12/n * sum of f(x, y, z), where x is in (-1, 1),  # y is in (3, 6), and z is in (0, 2)    x = (-1 - 1) * (rand(1, n)) + 1;  y = (6 - 3) * (rand(1, n)) + 3;  z = (2 - 0) * (rand(1, n));    sum = 0.0;    f = @(x, y, z) (y*x.^2 + z*log10(y) + e.^x);    for i = 1:n    sum = sum + f(x(i), y(i), z(i));    endfor;  sum = 12/n * sum;  printf("Sum = %f\n", sum)  endfunction