function hw2_sin(x, err)    if(err < eps("single"))    disp('Error must not be zero')  else  y = 0.0    for i=0:200      y = y + (-1)^i * ((x^(2*i + 1))) / factorial(2*i + 1)      error = (-1)^i * ((x^(2*i + 1))) / factorial(2*i + 1)      if(abs(error) < err)        #printf("Broke at i = %d", i)        break      endif    endfor  printf("Approximate value for sin(x) = %f", y)  end  